import { Grid, Dropdown, Button } from 'semantic-ui-react'
import React, { useEffect, useState } from 'react';


function SignUpForm ({ onFormSubmit }) {
    const initialData = {
        gym: '',
        tier: '',
        name: '',
        phone: '',
        email: ''
    }

    const [formData, setFormData] = useState(initialData)
    const [tiers, setTiers] = useState()

    useEffect(()=> {
        fetch('/tiers')
            .then(res => res.json())
            .then(setTiers)
    }, [])

    // const tiersArr = tiers.map(tierObj => {
    //     let dropdown = 
    //     return (
    //     {
    //         key: tierObj.name_of_tier,
    //         text: tierObj.description,
    //         value: tierObj.name_of_tier
    //     })
    // });

    let tiersDropdown = tiers.map((tier)=> {
        <option key={tier.id} >
            {tier.name_of_tier}
        </option>
    })


    const sampleArr = [
        {
            key: "Tier 1",
            text: "Tier 1",
            value: "Tier 1"
        }, 
        {
            key: "Tier 2",
            text: "Tier 2",
            value: "Tier 2"
        }]

    function handleChange (e) {
        setFormData({...formData, [e.target.name]: e.target.value})
    }

    function handleTierChange (e, value) {
        setFormData({...formData, [value.name]: value.value})

    }

    function handleSubmit (e) {
        e.preventDefault();

        fetch('/memberships', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData)
        })
        .then(res => res.json())
        .then(onFormSubmit)
        .then(setFormData(initialData))
    }



    return (
        <div>
            <Grid>
                <Grid.Column width={2}></Grid.Column>
                    <Grid.Column width={12}>
                        <form onSubmit={handleSubmit}  class="ui form">
                                <div class="field">
                                    <label style={{color: "white"}} >Gym</label>
                                    <div class="ui input">
                                        <input value={formData.gym} onChange={handleChange} name="gym" type="text" placeholder="Gym" />
                                    </div>
                                </div>
                                <select class="ui fluid dropdown" value="" type="select" name="tier" ></select>
                                <div>{tiersDropdown}</div>
                                <label style={{color: "white"}}>Tier</label>
                                <Dropdown
                                    name="tier"
                                    value={formData.tier}
                                    onChange={handleTierChange}
                                    placeholder='Select Tier'
                                    fluid
                                    selection
                                    options={sampleArr}
                                />
                                <div class="field">
                                    <label style={{color: "white"}} >Your Name</label>
                                    <div class="ui input">
                                        <input name="name" value={formData.name} onChange={handleChange} type="text" placeholder="Name" />
                                    </div>
                                </div>
                                <div class="field">
                                    <label style={{color: "white"}}>Phone Number</label>
                                    <div class="ui input">
                                        <input name='phone' value={formData.phone} onChange={handleChange} type="text" placeholder="Phone Number" />
                                    </div>
                                </div>
                                <div class="field">
                                    <label style={{color: "white"}} >Email</label>
                                    <div class="ui input">
                                        <input name="email" value={formData.email} onChange={handleChange} type="text" placeholder="Email" />
                                    </div>
                                </div>
                                <Button type='submit'>Finalize Membership</Button>
                        </form>
                    </Grid.Column>
                <Grid.Column width={2}></Grid.Column>
            </Grid>
        </div>
    )
}

export default SignUpForm;
